<?$this->load->view('admin/layouts/template_top_view');?><script>	$(function(){		$('#seo_feature_category').change(function() {			if($('#seo_feature_category').val() == "select_category")			{				$('#show_hide_categories').fadeIn('1000');			}			else			{				$('#show_hide_categories').fadeOut('1000');			}		});			//on page load set default value or show selected value		if($('#seo_feature_category').val() == "select_category")		{			$('#show_hide_categories').show();		}		else		{			$("#seo_category").attr('selectedIndex', 0);		}			//set selected page to database		//$('#page_name').val($("#seo_feature_category option:selected").text());	});		function before_form_post()	{		var page_name = $("#seo_feature_category option:selected").text();		if(page_name == "Select Category")		{			$('#page_name').val($("#seo_category option:selected").text());		}		else		{			$('#page_name').val(page_name);		}	}	</script>	<div class="grid">	<?=$this->load->view('admin/elements/seo_side_menu_view');?>		<!-- End Left Sidebar -->		<div class="right content_grid scrolldiv">			<div class="metro_col light">				<div id="msgz">						<?=print_msg();?>				</div>								<h2>Add</h2><br />					<form name ="add_seo_for_category" method="post" enctype="multipart/form-data" onsubmit="before_form_post();">				<div class="input left" style="width:44%; margin-right:10%;"> 					<?					$opt = array(								'select_name'		=> 'seo_feature_category',								'default'			=> 'Seo Feature Category',								'data_arr'			=> $this->config->item('seo_feature_list'),								'post_select'		=> (isset($_POST['seo_feature_category']))?$_POST['seo_feature_category']:'',								'multi_dimentional' => true								);					print build_dropdown( $opt );					?>					<span class="form_error"><?php echo form_error('seo_feature_category'); ?></span>					<input type="hidden" id="page_name" name="page_name" value="" />				</div>				<div id="show_hide_categories" class="select left" style="width:40%; display: none">					<?					$opt = array(								'select_name'		=> 'seo_category',								'default'			=> 'Category',								'data_arr'			=> $categories_parent_child_info,								'post_select'		=> (isset($_POST['seo_category']))?$_POST['seo_category']:'',								);					print build_dropdown( $opt );					?>				</div>				<div class="clr"></div>				<div class="input">					<label for="name">Meta Title *</label>					<input type="text" value="<?php echo set_value('meta_title'); ?>" name="meta_title" id="meta_title" />					<span class="form_error"><?php echo form_error('meta_title'); ?></span>				</div>                <div class="textarea">                	<label>Meta Description *</label>                    <textarea id="meta_description" name="meta_description"><?=trim(set_value('meta_description'));?></textarea>					<span class="form_error"><?php echo form_error('meta_description'); ?></span>                </div>                <div class="textarea">                	<label>Meta Keywords *</label>                    <textarea id="meta_keywords" name="meta_keywords"><?=trim(set_value('meta_keywords'));?></textarea>					<span class="form_error"><?php echo form_error('meta_keywords'); ?></span>                </div>								<div class="checkbox">					<input type="radio" name="prefix_postfix[]"  value="global_prefix" /><span>Global Prefix</span>					<input type="radio" name="prefix_postfix[]" value="global_postfix" /><span>Global Postfix</span>									</div>				<div class="button left" style="width:50%;">					<input type="submit" name = "submit_btn" id="submit_btn" value="Save"  class="publish" />				</div>  				<div class="clr"></div>				</form>			<div class="clr"></div>			</div>		<div class="clr"></div>  		</div>		</div><?$this->load->view('admin/layouts/template_bottom_view');?>