ame" name="page_name" value="<?=$seo_data['page_name']?>" />				</div>				<div id="show_hide_categories" class="input left" style="width:44%; display: none">					<?					$opt = array(								'select_name'		=> 'seo_category',								'default'			=> 'Category',								'data_arr'			=> $categories_parent_child_info,								'post_select'		=> (isset($_POST['seo_category']))?$_POST['seo_category']:$seo_data['category_id'],								);					print build_dropdown( $opt );					?>					<input type="hidden" id="hidden_seo_category_id" name="hidden_seo_category_id" value="<?=$seo_data['category_id']?>" />				</div>				<div class="clr"></div>				<div class="input">					<label for="name">Meta Title*</label>					<input type="text" value="<?php echo $seo_data['meta_title']; ?>" name="meta_title" id="meta_title" />					<span class="form_error"><?php echo form_error('meta_title'); ?></span>				</div>                <div class="textarea">                	<label>Meta Description</label>                    <textarea id="meta_description" name="meta_description"><?=trim($seo_data['meta_description']);?></textarea>					<span class="form_error"><?php echo form_error('meta_description'); ?></span>                </div>                <div class="textarea">                	<label>Meta Keywords</label>                    <textarea id="meta_keywords" name="meta_keywords"><?=trim($seo_data['meta_keywords']);?></textarea>					<span class="form_error"><?php echo form_error('meta_keywords'); ?></span>                </div>				<div class="checkbox">					<input type="radio" name="prefix_postfix[]" <?=(isset($seo_data['use_global_prefix']) && $seo_data['use_global_prefix'] == 'Y')?'checked':''?> value="global_prefix" /><span>Global Prefix</span>					<input type="radio" name="prefix_postfix[]" <?=(isset($seo_data['use_global_postfix']) && $seo_data['use_global_postfix'] == 'Y')?'checked':''?> value="global_postfix" /><span>Global Postfix</span>									</div>				<div class="button left" style="width:50%;">					<input type="submit" name="edit_btn" id="edit_btn" value="Save"  class="publish" />				</div>  				<div class="clr"></div>				</form>			<div class="clr"></div>			</div>		<div class="clr"></div>  		</div>		</div><?$this->load->view('admin/layouts/template_bottom_view');?>